import React, { useState } from "react";
import { GoogleOAuthProvider, GoogleLogin } from "@react-oauth/google";
import { useNavigate } from "react-router-dom"; // âœ… Use React Router for navigation
import axios from "axios"; // âœ… Import axios for API calls

const DoctorLogin = () => {
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [error, setError] = useState("");
    const navigate = useNavigate(); // âœ… Use React Router's useNavigate

    // âœ… Read environment variables
    const clientId = process.env.REACT_APP_GOOGLE_CLIENT_ID || "";
    const backendURL = process.env.REACT_APP_BACKEND_URL || "http://localhost:5008"; 

    // âœ… Handle Manual Login
    const handleManualLogin = async (e) => {
        e.preventDefault();
        try {
            const res = await axios.post(`${backendURL}/api/auth/doctor/login`, { email, password });

            localStorage.setItem("token", res.data.token);
            alert("Login successful!");
            navigate("/dashboard"); // âœ… Use navigate instead of router.push
        } catch (err) {
            setError(err.response?.data?.error || "Login failed. Please try again.");
        }
    };

    // âœ… Handle Google Login Success
    const handleGoogleSuccess = async (response) => {
        try {
            const { credential } = response;
            const res = await axios.post(`${backendURL}/api/auth/google`, { token: credential });

            localStorage.setItem("token", res.data.token);
            alert("Google login successful!");
            navigate("/dashboard");
        } catch (err) {
            setError("Google login failed. Please try again.");
        }
    };

    return (
        <GoogleOAuthProvider clientId={clientId}>
            <div className="login-container">
                <h2>Doctor Login</h2>

                {error && <p className="error">{error}</p>}

                {/* ðŸ”¹ Manual Login Form */}
                <form onSubmit={handleManualLogin} className="login-form">
                    <input 
                        type="email" 
                        placeholder="Email" 
                        value={email} 
                        onChange={(e) => setEmail(e.target.value)} 
                        required
                    />
                    <input 
                        type="password" 
                        placeholder="Password" 
                        value={password} 
                        onChange={(e) => setPassword(e.target.value)} 
                        required
                    />
                    <button type="submit">Login</button>
                </form>

                <div className="divider">OR</div>

                {/* ðŸ”¹ Google Login Button */}
                <GoogleLogin onSuccess={handleGoogleSuccess} onError={() => setError("Google Login Failed")} />

                {/* ðŸ”¹ Registration Navigation */}
                <p>
                    Don't have an account? <button onClick={() => navigate("/doctor-register")}>Register</button>
                </p>
            </div>
        </GoogleOAuthProvider>
    );
};

export default DoctorLogin;
