import React, { useEffect, useState } from "react";
import { useLocation } from "react-router-dom";
import axios from "axios";
import { GoogleMap, LoadScript, Marker } from "@react-google-maps/api";
import "../styles/DoctorSearch.css";

const SearchResults = () => {
    const location = useLocation();
    const searchParams = new URLSearchParams(location.search);
    const query = searchParams.get("q") || "";
    const city = searchParams.get("location") || "İzmir";
    const [doctors, setDoctors] = useState([]);
    const API_URL = "http://localhost:5008";
    const clientId = "934186764034-hob9fe241h4ltkql4j3dukl8h8qc5pa0.apps.googleusercontent.com";

    useEffect(() => {
        const fetchDoctors = async () => {
            try {
                const response = await axios.get(
                    `${API_URL}/api/doctors/search?q=${query}&location=${city}`
                );
                setDoctors(response.data);
            } catch (error) {
                console.error("Error fetching doctors:", error);
            }
        };
        fetchDoctors();
    }, [query, city]);

    // Function to get current week's available times
    const getCurrentWeekTimes = (availableTimes) => {
        const today = new Date();
        const endOfWeek = new Date();
        endOfWeek.setDate(today.getDate() + 6); // Current week (next 6 days)

        return availableTimes.filter(time => {
            const timeDate = new Date(time.date);
            return timeDate >= today && timeDate <= endOfWeek;
        });
    };

    return (
        <div className="results-container">
            <div className="results-header">
                <h3>{query}, {city}</h3>
                <p>Herhangi bir ek ücret ödemeden randevu alın.</p>
            </div>

            {doctors.length > 0 ? (
                doctors.map((doctor) => (
                    <div key={doctor.id} className="doctor-card">
                        <div className="doctor-info">
                            <img src={doctor.image} alt={doctor.name} className="doctor-image"/>
                            <div>
                                <h4>{doctor.name} ✅</h4>
                                <p>{doctor.specialization}</p>
                                <p>{doctor.city}</p>
                                <p>⭐ {doctor.reviews} Görüş</p>
                            </div>
                        </div>
                        <div className="appointment-times">
                            {getCurrentWeekTimes(doctor.availableTimes).map((time, index) => (
                                <button key={index}>{time.time}</button>
                            ))}
                        </div>
                    </div>
                ))
            ) : (
                <p>Sonuç bulunamadı.</p>
            )}

            <div className="map-container">
                <LoadScript googleMapsApiKey="AIzaSyBAaYDx5l5OTNGC-qWyo4VCwhpuygOhuAo">
                    <GoogleMap
                        mapContainerClassName="google-map"
                        center={{ lat: 38.4192, lng: 27.1287 }}
                        zoom={12}
                    >
                        {doctors.map((doctor) => (
                            <Marker key={doctor.id} position={{ lat: doctor.lat, lng: doctor.lng }} />
                        ))}
                    </GoogleMap>
                </LoadScript>
            </div>
        </div>
    );
};

export default SearchResults;
